import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const RecruiterSignup = () => {
  const navigate = useNavigate();
  const { signupRecruiter } = useAuth();
  const [activeTab, setActiveTab] = useState('recruteur');
  const [formData, setFormData] = useState({
    companyName: '',
    sector: '',
    companySize: '',
    website: '',
    fullName: '',
    position: '',
    email: '',
    password: '',
    confirmPassword: '',
    phoneNumber: '',
    termsAccepted: false
  });
  const [passwordError, setPasswordError] = useState('');
  const [stars, setStars] = useState([]);

  // Génération des étoiles pour le background
  useEffect(() => {
    const generateStars = () => {
      const newStars = [];
      for (let i = 0; i < 100; i++) {
        newStars.push({
          id: i,
          left: `${Math.random() * 100}%`,
          top: `${Math.random() * 100}%`,
          size: `${Math.random() * 2}px`,
          duration: `${Math.random() * 5 + 3}s`
        });
      }
      setStars(newStars);
    };
    
    generateStars();
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value
    }));

    // Réinitialiser l'erreur de mot de passe lors de la modification des champs
    if (name === 'password' || name === 'confirmPassword') {
      setPasswordError('');
    }
  };

  const validateForm = () => {
    // Validation du formulaire
    if (!formData.companyName || !formData.fullName || !formData.email) {
      setPasswordError('Veuillez remplir tous les champs obligatoires');
      return false;
    }

    if (formData.password !== formData.confirmPassword) {
      setPasswordError('Les mots de passe ne correspondent pas');
      return false;
    }
    
    if (formData.password.length < 8) {
      setPasswordError('Le mot de passe doit contenir au moins 8 caractères');
      return false;
    }

    if (!formData.termsAccepted) {
      setPasswordError('Vous devez accepter les conditions générales');
      return false;
    }
    
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validation avant soumission
    if (!validateForm()) {
      return;
    }
    
    try {
      // Préparer les données du recruteur
      const recruiterData = {
        companyName: formData.companyName,
        sector: formData.sector,
        companySize: formData.companySize,
        website: formData.website,
        fullName: formData.fullName,
        position: formData.position,
        email: formData.email,
        phoneNumber: formData.phoneNumber,
        type: 'recruiter'
      };
      
      // Appeler la fonction d'inscription recruteur
      await signupRecruiter(recruiterData);
      
      // Rediriger vers le tableau de bord recruteur
      navigate('/recruiter-dashboard');
    } catch (error) {
      console.error("Erreur lors de l'inscription:", error);
      setPasswordError('Erreur lors de la création du compte');
    }
  };

  return (
    <>
      <Navbar />
      
      {/* Stars background */}
      <div className="stars">
        {stars.map((star) => (
          <div
            key={star.id}
            className="star"
            style={{
              left: star.left,
              top: star.top,
              width: star.size,
              height: star.size,
              animation: `twinkle ${star.duration} infinite`
            }}
          />
        ))}
      </div>
      
      <div className="signup-container">
        <div className="signup-card">
          {/* Le reste du code reste identique */}
          <div className="signup-left">
            <div className="tabs">
              <Link 
                to="/login" 
                className={`tab ${activeTab === 'connexion' ? 'active' : ''}`}
              >
                Connexion
              </Link>
              <button 
                className={`tab ${activeTab === 'recruteur' ? 'active' : ''}`}
                onClick={() => setActiveTab('recruteur')}
              >
                Recruteur
              </button>
              <Link 
                to="/candidate-signup" 
                className={`tab ${activeTab === 'candidat' ? 'active' : ''}`}
              >
                Candidat
              </Link>
            </div>
            
            <h2>Créer un compte Recruteur</h2>
            
            {passwordError && (
              <div className="login-error">
                {passwordError}
              </div>
            )}
            
            <form onSubmit={handleSubmit}>
              {/* Pour simplifier, nous ne montrons pas tous les champs de formulaire ici */}
              {/* Mais dans le code réel, vous devriez utiliser handleChange pour tous les champs */}
              <div className="form-group">
                <label>Nom de l'entreprise</label>
                <input
                  type="text"
                  name="companyName"
                  value={formData.companyName}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Email</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Mot de passe</label>
                <input
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-check">
                <input
                  type="checkbox"
                  name="termsAccepted"
                  checked={formData.termsAccepted}
                  onChange={handleChange}
                  required
                />
                <label>J'accepte les conditions générales</label>
              </div>
              <button 
                type="submit" 
                className="signup-btn"
                disabled={!formData.termsAccepted}
              >
                Créer mon compte
              </button>
            </form>
          </div>
          
          <div className="signup-right">
            {/* Contenu de droite */}
            <h2>Avantages Recruteur</h2>
            <div className="benefits-list">
              <div className="benefit-item">
                <div className="benefit-icon">✓</div>
                <div className="benefit-content">
                  <h4>Matching IA de précision</h4>
                  <p>Notre algorithme trouve les meilleurs candidats pour vos postes.</p>
                </div>
              </div>
              <div className="benefit-item">
                <div className="benefit-icon">✓</div>
                <div className="benefit-content">
                  <h4>Gain de temps considérable</h4>
                  <p>Réduisez votre temps de recrutement de 60% en moyenne.</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default RecruiterSignup;
