import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import { Link } from 'react-router-dom';

const RecruiterSignup = () => {
  const [activeTab, setActiveTab] = useState('recruteur');
  const [formData, setFormData] = useState({
    companyName: '',
    sector: '',
    companySize: '',
    website: '',
    fullName: '',
    position: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [passwordError, setPasswordError] = useState('');
  const [stars, setStars] = useState([]);

  // Génération des étoiles pour le background
  useEffect(() => {
    const generateStars = () => {
      const newStars = [];
      for (let i = 0; i < 100; i++) {
        newStars.push({
          id: i,
          left: `${Math.random() * 100}%`,
          top: `${Math.random() * 100}%`,
          size: `${Math.random() * 2}px`,
          duration: `${Math.random() * 5 + 3}s`
        });
      }
      setStars(newStars);
    };
    
    generateStars();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));

    // Réinitialiser l'erreur de mot de passe lors de la modification des champs
    if (name === 'password' || name === 'confirmPassword') {
      setPasswordError('');
    }
  };

  const validatePasswords = () => {
    if (formData.password !== formData.confirmPassword) {
      setPasswordError('Les mots de passe ne correspondent pas');
      return false;
    }
    
    if (formData.password.length < 8) {
      setPasswordError('Le mot de passe doit contenir au moins 8 caractères');
      return false;
    }
    
    setPasswordError('');
    return true;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!validatePasswords()) {
      return; // Arrêter la soumission si les mots de passe ne sont pas valides
    }
    
    console.log('Formulaire soumis:', formData);
    // Ici, vous pourriez ajouter la logique pour créer un compte avec Firebase
  };

  return (
    <>
      <Navbar />
      
      {/* Stars background */}
      <div className="stars">
        {stars.map((star) => (
          <div
            key={star.id}
            className="star"
            style={{
              left: star.left,
              top: star.top,
              width: star.size,
              height: star.size,
              animation: `twinkle ${star.duration} infinite`
            }}
          />
        ))}
      </div>
      
      <div className="signup-container">
        <div className="signup-card">
          <div className="signup-left">
            <div className="tabs">
              <Link 
                to="/login" 
                className={`tab ${activeTab === 'connexion' ? 'active' : ''}`}
              >
                Connexion
              </Link>
              <button 
                className={`tab ${activeTab === 'recruteur' ? 'active' : ''}`}
                onClick={() => setActiveTab('recruteur')}
              >
                Recruteur
              </button>
              <Link 
                to="/candidate-signup" 
                className={`tab ${activeTab === 'candidat' ? 'active' : ''}`}
              >
                Candidat
              </Link>
            </div>
            
            <h2>Créer un compte Recruteur</h2>
            <p className="form-description">Remplissez ce formulaire pour créer votre profil et commencer</p>
            
            <form onSubmit={handleSubmit}>
              <div className="form-section">
                <h3>Informations de l'entreprise</h3>
                
                <div className="form-group">
                  <label htmlFor="companyName">Nom de l'entreprise</label>
                  <input
                    type="text"
                    id="companyName"
                    name="companyName"
                    className="form-control"
                    placeholder="TechCorp SARL"
                    value={formData.companyName}
                    onChange={handleChange}
                    required
                  />
                </div>
                
                <div className="form-group">
                  <label htmlFor="sector">Secteur d'activité</label>
                  <input
                    type="text"
                    id="sector"
                    name="sector"
                    className="form-control"
                    placeholder="Technologie/IT"
                    value={formData.sector}
                    onChange={handleChange}
                    required
                  />
                </div>
                
                <div className="form-group">
                  <label htmlFor="companySize">Taille de l'entreprise</label>
                  <input
                    type="text"
                    id="companySize"
                    name="companySize"
                    className="form-control"
                    placeholder="10-50 employés"
                    value={formData.companySize}
                    onChange={handleChange}
                    required
                  />
                </div>
                
                <div className="form-group">
                  <label htmlFor="website">Site web</label>
                  <input
                    type="url"
                    id="website"
                    name="website"
                    className="form-control"
                    placeholder="www.techcorp.com"
                    value={formData.website}
                    onChange={handleChange}
                  />
                </div>
              </div>
              
              <div className="form-section">
                <h3>Informations personnelles</h3>
                
                <div className="form-group">
                  <label htmlFor="fullName">Nom</label>
                  <input
                    type="text"
                    id="fullName"
                    name="fullName"
                    className="form-control"
                    placeholder="Martin Dupont"
                    value={formData.fullName}
                    onChange={handleChange}
                    required
                  />
                </div>
                
                <div className="form-group">
                  <label htmlFor="position">Poste</label>
                  <input
                    type="text"
                    id="position"
                    name="position"
                    className="form-control"
                    placeholder="Directeur des Ressources Humaines"
                    value={formData.position}
                    onChange={handleChange}
                    required
                  />
                </div>
                
                <div className="form-group">
                  <label htmlFor="email">Email professionnel</label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    className="form-control"
                    placeholder="martin.dupont@techcorp.com"
                    value={formData.email}
                    onChange={handleChange}
                    required
                  />
                </div>
                
                <div className="form-group">
                  <label htmlFor="password">Mot de passe</label>
                  <input
                    type="password"
                    id="password"
                    name="password"
                    className="form-control"
                    placeholder="Votre mot de passe"
                    value={formData.password}
                    onChange={handleChange}
                    required
                  />
                </div>
                
                <div className="form-group">
                  <label htmlFor="confirmPassword">Confirmer le mot de passe</label>
                  <input
                    type="password"
                    id="confirmPassword"
                    name="confirmPassword"
                    className="form-control"
                    placeholder="Confirmez votre mot de passe"
                    value={formData.confirmPassword}
                    onChange={handleChange}
                    required
                  />
                </div>
                
                {passwordError && (
                  <div className="password-error">
                    {passwordError}
                  </div>
                )}
              </div>
              
              <button type="submit" className="signup-btn">
                Créer mon compte
              </button>
            </form>
          </div>
          
          <div className="signup-right">
            <h2>Avantages Recruteur</h2>
            
            <div className="benefits-list">
              <div className="benefit-item">
                <div className="benefit-icon">✓</div>
                <div className="benefit-content">
                  <h4>Matching IA précis des candidats</h4>
                  <p>Notre algorithme analyse les compétences et expériences</p>
                </div>
              </div>
              
              <div className="benefit-item">
                <div className="benefit-icon">✓</div>
                <div className="benefit-content">
                  <h4>Dashboard analytique intelligent</h4>
                  <p>Suivez vos recrutements et performances</p>
                </div>
              </div>
              
              <div className="benefit-item">
                <div className="benefit-icon">✓</div>
                <div className="benefit-content">
                  <h4>API REST pour intégration</h4>
                  <p>Connectez-vous avec vos outils existants</p>
                </div>
              </div>
              
              <div className="benefit-item">
                <div className="benefit-icon">✓</div>
                <div className="benefit-content">
                  <h4>Suivi des candidatures</h4>
                  <p>Gérez tout le processus de recrutement</p>
                </div>
              </div>
              
              <div className="benefit-item">
                <div className="benefit-icon">✓</div>
                <div className="benefit-content">
                  <h4>Analyse prédictive des profils</h4>
                  <p>Identifiez les meilleurs talents pour vos postes</p>
                </div>
              </div>
            </div>
            
            <div className="matching-rate">
              <span className="rate-value">85%</span>
              <p className="rate-description">taux moyen de compatibilité avec l'IA de matching</p>
            </div>
            
            <a href="/demo" className="demo-btn">Voir la démo</a>
          </div>
        </div>
      </div>
    </>
  );
};

export default RecruiterSignup;
