import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const RecruiterSignup = () => {
  const navigate = useNavigate();
  const { signupRecruiter } = useAuth();
  const [activeTab, setActiveTab] = useState('recruteur');
  const [formData, setFormData] = useState({
    companyName: '',
    sector: '',
    companySize: '',
    website: '',
    fullName: '',
    position: '',
    email: '',
    password: '',
    confirmPassword: '',
    phoneNumber: '',
    termsAccepted: false
  });
  const [passwordError, setPasswordError] = useState('');
  const [stars, setStars] = useState([]);

  // Génération des étoiles pour le background
  useEffect(() => {
    const generateStars = () => {
      const newStars = [];
      for (let i = 0; i < 100; i++) {
        newStars.push({
          id: i,
          left: `${Math.random() * 100}%`,
          top: `${Math.random() * 100}%`,
          size: `${Math.random() * 2}px`,
          duration: `${Math.random() * 5 + 3}s`
        });
      }
      setStars(newStars);
    };
    
    generateStars();
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value
    }));

    // Réinitialiser l'erreur de mot de passe lors de la modification des champs
    if (name === 'password' || name === 'confirmPassword') {
      setPasswordError('');
    }
  };

  const validateForm = () => {
    // Validation du formulaire
    if (!formData.companyName || !formData.fullName || !formData.email) {
      setPasswordError('Veuillez remplir tous les champs obligatoires');
      return false;
    }

    if (formData.password !== formData.confirmPassword) {
      setPasswordError('Les mots de passe ne correspondent pas');
      return false;
    }
    
    if (formData.password.length < 8) {
      setPasswordError('Le mot de passe doit contenir au moins 8 caractères');
      return false;
    }

    if (!formData.termsAccepted) {
      setPasswordError('Vous devez accepter les conditions générales');
      return false;
    }
    
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validation avant soumission
    if (!validateForm()) {
      return;
    }
    
    try {
      // Préparer les données du recruteur
      const recruiterData = {
        companyName: formData.companyName,
        sector: formData.sector,
        companySize: formData.companySize,
        website: formData.website,
        fullName: formData.fullName,
        position: formData.position,
        email: formData.email,
        phoneNumber: formData.phoneNumber,
        type: 'recruiter'
      };
      
      // Appeler la fonction d'inscription recruteur
      await signupRecruiter(recruiterData);
    } catch (error) {
      console.error("Erreur lors de l'inscription:", error);
      setPasswordError('Erreur lors de la création du compte');
    }
  };

  return (
    <>
      <Navbar />
      
      {/* Stars background */}
      <div className="stars">
        {stars.map((star) => (
          <div
            key={star.id}
            className="star"
            style={{
              left: star.left,
              top: star.top,
              width: star.size,
              height: star.size,
              animation: `twinkle ${star.duration} infinite`
            }}
          />
        ))}
      </div>
      
      <div className="signup-container">
        <div className="signup-card">
          <div className="signup-left">
            <div className="tabs">
              <Link 
                to="/login" 
                className={`tab ${activeTab === 'connexion' ? 'active' : ''}`}
              >
                Connexion
              </Link>
              <button 
                className={`tab ${activeTab === 'recruteur' ? 'active' : ''}`}
                onClick={() => setActiveTab('recruteur')}
              >
                Recruteur
              </button>
              <Link 
                to="/candidate-signup" 
                className={`tab ${activeTab === 'candidat' ? 'active' : ''}`}
              >
                Candidat
              </Link>
            </div>
            
            <h2>Créer un compte Recruteur</h2>
            <p className="form-description">
              Rejoignez TalentMatch et simplifiez votre processus de recrutement grâce à notre IA intelligente.
            </p>
            
            {passwordError && (
              <div className="login-error">
                {passwordError}
              </div>
            )}
            
            <form onSubmit={handleSubmit}>
              <div className="form-section">
                <h3>Informations de l'entreprise</h3>
                
                <div className="form-group">
                  <label htmlFor="companyName">Nom de l'entreprise *</label>
                  <input
                    type="text"
                    id="companyName"
                    name="companyName"
                    className="form-control"
                    placeholder="Nom de votre entreprise"
                    value={formData.companyName}
                    onChange={handleChange}
                    required
                  />
                </div>
                
                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="sector">Secteur d'activité</label>
                    <input
                      type="text"
                      id="sector"
                      name="sector"
                      className="form-control"
                      placeholder="Ex: Technologies, Finance, Santé"
                      value={formData.sector}
                      onChange={handleChange}
                    />
                  </div>
                  
                  <div className="form-group">
                    <label htmlFor="companySize">Taille de l'entreprise</label>
                    <select
                      id="companySize"
                      name="companySize"
                      className="form-control"
                      value={formData.companySize}
                      onChange={handleChange}
                    >
                      <option value="">Sélectionnez</option>
                      <option value="1-10">1-10 employés</option>
                      <option value="11-50">11-50 employés</option>
                      <option value="51-200">51-200 employés</option>
                      <option value="201-500">201-500 employés</option>
                      <option value="500+">Plus de 500 employés</option>
                    </select>
                  </div>
                </div>
                
                <div className="form-group">
                  <label htmlFor="website">Site web de l'entreprise</label>
                  <input
                    type="url"
                    id="website"
                    name="website"
                    className="form-control"
                    placeholder="https://www.entreprise.com"
                    value={formData.website}
                    onChange={handleChange}
                  />
                </div>
              </div>
              
              <div className="form-section">
                <h3>Informations personnelles</h3>
                
                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="fullName">Nom complet *</label>
                    <input
                      type="text"
                      id="fullName"
                      name="fullName"
                      className="form-control"
                      placeholder="Votre nom et prénom"
                      value={formData.fullName}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  
                  <div className="form-group">
                    <label htmlFor="position">Votre poste</label>
                    <input
                      type="text"
                      id="position"
                      name="position"
                      className="form-control"
                      placeholder="Ex: DRH, Recruteur, Responsable RH"
                      value={formData.position}
                      onChange={handleChange}
                    />
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="email">Email professionnel *</label>
                    <input
                      type="email"
                      id="email"
                      name="email"
                      className="form-control"
                      placeholder="votre.email@entreprise.com"
                      value={formData.email}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  
                  <div className="form-group">
                    <label htmlFor="phoneNumber">Téléphone</label>
                    <input
                      type="tel"
                      id="phoneNumber"
                      name="phoneNumber"
                      className="form-control"
                      placeholder="Numéro de téléphone"
                      value={formData.phoneNumber}
                      onChange={handleChange}
                    />
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="password">Mot de passe *</label>
                    <input
                      type="password"
                      id="password"
                      name="password"
                      className="form-control"
                      placeholder="Créez un mot de passe sécurisé"
                      value={formData.password}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  
                  <div className="form-group">
                    <label htmlFor="confirmPassword">Confirmez le mot de passe *</label>
                    <input
                      type="password"
                      id="confirmPassword"
                      name="confirmPassword"
                      className="form-control"
                      placeholder="Répétez votre mot de passe"
                      value={formData.confirmPassword}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
              </div>
              
              <div className="form-section">
                <div className="form-check">
                  <label className="custom-checkbox">
                    <input
                      type="checkbox"
                      name="termsAccepted"
                      checked={formData.termsAccepted}
                      onChange={handleChange}
                    />
                    <span className="checkmark"></span>
                    J'accepte les conditions générales d'utilisation
                  </label>
                </div>
              </div>
              
              <button 
                type="submit" 
                className="signup-btn"
                disabled={!formData.termsAccepted}
              >
                Créer mon compte
              </button>
            </form>
          </div>
          
          <div className="signup-right">
            <h2>Avantages Recruteur</h2>
            
            <div className="benefits-list">
              <div className="benefit-item">
                <div className="benefit-icon">1</div>
                <div className="benefit-content">
                  <h4>Matching IA précis</h4>
                  <p>Notre algorithme analyse en profondeur les compétences des candidats</p>
                </div>
              </div>
              
              <div className="benefit-item">
                <div className="benefit-icon">2</div>
                <div className="benefit-content">
                  <h4>Tableau de bord analytique</h4>
                  <p>Suivez vos recrutements avec des statistiques détaillées</p>
                </div>
              </div>
              
              <div className="benefit-item">
                <div className="benefit-icon">3</div>
                <div className="benefit-content">
                  <h4>Intégration facile</h4>
                  <p>Compatible avec vos outils RH existants</p>
                </div>
              </div>
            </div>
            
            <div className="matching-rate">
              <span className="rate-value">85%</span>
              <p className="rate-description">taux moyen de compatibilité candidat</p>
            </div>
            
            <Link to="/demo" className="demo-btn">
              Voir la démo
            </Link>
          </div>
        </div>
      </div>
    </>
  );
};

export default RecruiterSignup;
