import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import Login from './pages/Login';
import RecruiterSignup from './pages/RecruiterSignup';
import CandidateSignup from './pages/CandidateSignup';
import LinkedInCallback from './pages/LinkedInCallback';
import LinkedInProfile from './pages/LinkedInProfile';
import RecruiterDashboard from './pages/RecruiterDashboard';
import CandidateDashboard from './pages/CandidateDashboard';
import TalentMatchDemo from './components/TalentMatchDemo';

// Composant pour les routes protégées des recruteurs
function RecruiterRoute({ children }) {
  const { currentUser, userType, loading } = useAuth();

  if (loading) {
    return <div>Chargement...</div>;
  }

  if (!currentUser) {
    return <Navigate to="/login" />;
  }

  if (userType !== 'recruiter') {
    return <Navigate to="/unauthorized" />;
  }

  return children;
}

// Composant pour les routes protégées des candidats
function CandidateRoute({ children }) {
  const { currentUser, userType, loading } = useAuth();

  if (loading) {
    return <div>Chargement...</div>;
  }

  if (!currentUser) {
    return <Navigate to="/login" />;
  }

  if (userType !== 'candidate') {
    return <Navigate to="/unauthorized" />;
  }

  return children;
}

// Composant qui redirige l'utilisateur selon son type
function UserRedirect() {
  const { currentUser, userType, loading } = useAuth();

  if (loading) {
    return <div>Chargement...</div>;
  }

  if (!currentUser) {
    return <Navigate to="/login" />;
  }

  if (userType === 'recruiter') {
    return <Navigate to="/recruiter-dashboard" />;
  }

  if (userType === 'candidate') {
    return <Navigate to="/candidate-dashboard" />;
  }

  return <Navigate to="/login" />;
}

// Page pour accès non autorisé
const Unauthorized = () => (
  <div style={{ padding: '2rem', color: 'white', maxWidth: '800px', margin: '0 auto' }}>
    <h1>Accès non autorisé</h1>
    <p>Vous n'avez pas les droits nécessaires pour accéder à cette page.</p>
  </div>
);

function App() {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          <Route path="/" element={<UserRedirect />} />
          <Route path="/login" element={<Login />} />
          <Route path="/recruiter-signup" element={<RecruiterSignup />} />
          <Route path="/candidate-signup" element={<CandidateSignup />} />
          <Route path="/linkedin-callback" element={<LinkedInCallback />} />
          <Route path="/linkedin-profile" element={<LinkedInProfile />} />
          <Route path="/demo" element={<TalentMatchDemo />} />

          {/* Routes protégées */}
          <Route 
            path="/recruiter-dashboard" 
            element={
              <RecruiterRoute>
                <RecruiterDashboard />
              </RecruiterRoute>
            } 
          />
          <Route 
            path="/candidate-dashboard" 
            element={
              <CandidateRoute>
                <CandidateDashboard />
              </CandidateRoute>
            } 
          />

          {/* Routes de placeholder */}
          <Route path="/manual-profile" element={<div>Page de création de profil manuel</div>} />
          <Route path="/ai-profile-assistance" element={<div>Aide IA pour créer votre profil</div>} />
          <Route path="/unauthorized" element={<Unauthorized />} />
          <Route path="/entreprises" element={<div>Page des entreprises</div>} />
          <Route path="/offres" element={<div>Page des offres</div>} />
          <Route path="/candidats" element={<div>Page des candidats</div>} />
          <Route path="/conseils" element={<div>Page des conseils</div>} />
          <Route path="/forgot-password" element={<div>Mot de passe oublié</div>} />

          {/* Redirection par défaut */}
          <Route path="*" element={<Navigate to="/login" />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

export default App;
