import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const MonProfil = () => {
  const { currentUser } = useAuth();
  const [stars, setStars] = useState([]);
  const [activeSection, setActiveSection] = useState('infos');
  const [editMode, setEditMode] = useState(false);
const [profileData, setProfileData] = useState({
  firstName: currentUser?.profile?.firstName || 'Jean',
  lastName: currentUser?.profile?.lastName || 'Dupont',
  headline: 'Développeur Full Stack',
  location: 'Paris, France',
  email: currentUser?.email || 'jean.dupont@example.com',
  phone: '+33 6 12 34 56 78'
});
  
  // États vides prêts à recevoir des données du backend
  const [infoPerso, setInfoPerso] = useState({});
  const [experiences, setExperiences] = useState([]);
  const [formations, setFormations] = useState([]);
  const [competences, setCompetences] = useState([]);
  const [langues, setLangues] = useState([]);
  const [visibiliteParams, setVisibiliteParams] = useState({});
  const [mode, setMode] = useState('visualisation');

  // Génération des étoiles pour le background
  useEffect(() => {
    const generateStars = () => {
      const newStars = [];
      for (let i = 0; i < 100; i++) {
        newStars.push({
          id: i,
          left: `${Math.random() * 100}%`,
          top: `${Math.random() * 100}%`,
          size: `${Math.random() * 2}px`,
          duration: `${Math.random() * 5 + 3}s`
        });
      }
      setStars(newStars);
    };
    
    generateStars();
  }, []);

  // Cette fonction sera implémentée par le backend pour formater les dates
  const formatDate = (dateString) => {
    if (!dateString) return 'Présent';
    return dateString;
  };
  const handleEditProfile = () => {
    setEditMode(true);
  };
  
  const handleSaveProfile = () => {
    setEditMode(false);
    // Simuler une mise à jour dans le AuthContext
    alert("Profil mis à jour avec succès!");
  };

  // Cette fonction sera connectée au backend pour enregistrer les modifications
  const handleSubmit = (e) => {
    e.preventDefault();
    setMode('visualisation');
    // Le backend implementera la logique pour enregistrer les modifications
  };

  return (
    <div className="dashboard-container">
      {/* Background stars */}
      <div className="stars">
        {stars.map((star) => (
          <div
            key={star.id}
            className="star"
            style={{
              left: star.left,
              top: star.top,
              width: star.size,
              height: star.size,
              animation: `twinkle ${star.duration} infinite`
            }}
          />
        ))}
      </div>

      {/* Structure de la page sans données */}
      <header className="dashboard-header">
        {/* En-tête */}
      </header>

      <div className="dashboard-content">
        <aside className="dashboard-sidebar">
          {/* Sidebar */}
        </aside>

        <main className="dashboard-main">
          <div className="profile-container">
            <div className="profile-header">
              <div className="profile-photo">
                <div className="profile-initials"></div>
              </div>
              <div className="profile-title">
                <h1 className="profile-name"></h1>
                <p className="profile-poste"></p>
                <p className="profile-localisation"></p>
              </div>
              <div className="profile-actions">
                <button className="profile-action-btn download-btn">
                  <i className="fas fa-download"></i>
                  Télécharger CV
                </button>
                <button className="profile-action-btn share-btn">
                  <i className="fas fa-share-alt"></i>
                  Partager
                </button>
                <button className="profile-action-btn preview-btn">
                  <i className="fas fa-eye"></i>
                  Aperçu public
                </button>
              </div>
            </div>

            <div className="profile-nav">
              <button 
                className={`profile-nav-btn ${activeSection === 'infos' ? 'active' : ''}`}
                onClick={() => setActiveSection('infos')}
              >
                <i className="fas fa-user"></i>
                Informations
              </button>
              {/* Autres onglets de navigation */}
            </div>

            <div className="profile-content">
              {/* Le contenu sera rempli dynamiquement par le backend */}
              <div className="empty-profile-message">
                <p>Chargement du profil...</p>
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default MonProfil;
