import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import Login from './pages/Login';
import RecruiterSignup from './pages/RecruiterSignup';
import CandidateSignup from './pages/CandidateSignup';
import LinkedInCallback from './pages/LinkedInCallback';
import LinkedInProfile from './pages/LinkedInProfile';
import RecruiterDashboard from './pages/RecruiterDashboard';
import CandidateDashboard from './pages/CandidateDashboard';
import TalentMatchDemo from './components/TalentMatchDemo';
import MonProfil from './pages/MonProfil';

// Composant pour les routes protégées
const ProtectedRoute = ({ children, allowedTypes }) => {
  const { currentUser, userType } = useAuth();

  if (!currentUser) {
    return <Navigate to="/login" />;
  }

  if (allowedTypes && !allowedTypes.includes(userType)) {
    return <Navigate to="/unauthorized" />;
  }

  return children;
};

function App() {
  return (
    <Router>
      <AuthProvider>
        <Routes>
          {/* Routes publiques */}
          <Route path="/" element={<Login />} />
          <Route path="/login" element={<Login />} />
          <Route path="/recruiter-signup" element={<RecruiterSignup />} />
          <Route path="/candidate-signup" element={<CandidateSignup />} />
          <Route path="/demo" element={<TalentMatchDemo />} />
          <Route path="/linkedin-callback" element={<LinkedInCallback />} />

          {/* Routes protégées */}
          <Route 
            path="/recruiter-dashboard" 
            element={
              <ProtectedRoute allowedTypes={['recruiter']}>
                <RecruiterDashboard />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/candidate-dashboard" 
            element={
              <ProtectedRoute allowedTypes={['candidate']}>
                <CandidateDashboard />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/linkedin-profile" 
            element={
              <ProtectedRoute allowedTypes={['candidate']}>
                <LinkedInProfile />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/mon-profil" 
            element={
              <ProtectedRoute allowedTypes={['candidate']}>
                <MonProfil />
              </ProtectedRoute>
            } 
          />

          {/* Placeholder pour accès non autorisé */}
          <Route 
            path="/unauthorized" 
            element={<div>Accès non autorisé</div>} 
          />

          {/* Redirection par défaut */}
          <Route path="*" element={<Navigate to="/login" />} />
        </Routes>
      </AuthProvider>
    </Router>
  );
}

export default App;
