import React, { useState, useEffect } from 'react';

const TalentMatchDemo = () => {
  const [activeTab, setActiveTab] = useState('connexion');
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    rememberMe: false
  });
  const [stars, setStars] = useState([]);

  useEffect(() => {
    // Générer les étoiles pour le background
    const newStars = [];
    for (let i = 0; i < 100; i++) {
      newStars.push({
        id: i,
        left: `${Math.random() * 100}%`,
        top: `${Math.random() * 100}%`,
        size: `${Math.random() * 2}px`,
        duration: `${Math.random() * 5 + 3}s`
      });
    }
    setStars(newStars);
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Connexion avec: ${formData.email}`);
  };

  return (
    <div className="bg-purple-950 min-h-screen text-white relative overflow-hidden">
      {/* Stars background */}
      <div className="absolute inset-0 z-0">
        {stars.map((star) => (
          <div
            key={star.id}
            className="absolute rounded-full bg-white opacity-70"
            style={{
              left: star.left,
              top: star.top,
              width: star.size,
              height: star.size,
              animation: `pulse ${star.duration} infinite ease-in-out`
            }}
          />
        ))}
      </div>
      
      {/* Navbar */}
      <nav className="flex justify-between items-center p-4 relative z-10">
        <div className="flex items-center">
          <div className="w-10 h-10 bg-purple-500 rounded-full flex items-center justify-center font-bold mr-2">TM</div>
          <span className="font-semibold text-xl">TalentMatch</span>
        </div>
        <div className="flex gap-8">
          <a href="#" className="hover:text-purple-400 transition-colors">Entreprises</a>
          <a href="#" className="hover:text-purple-400 transition-colors">Offres</a>
          <a href="#" className="hover:text-purple-400 transition-colors">Candidats</a>
          <a href="#" className="hover:text-purple-400 transition-colors">Conseils</a>
        </div>
      </nav>
      
      {/* Login Container */}
      <div className="flex justify-center items-center min-h-[calc(100vh-80px)] p-8">
        <div className="bg-gray-900 bg-opacity-80 rounded-3xl overflow-hidden w-full max-w-4xl flex shadow-lg">
          {/* Left Side */}
          <div className="w-2/5 p-10 flex flex-col items-center justify-center text-center relative">
            <div className="w-24 h-24 bg-purple-500 rounded-full flex items-center justify-center text-3xl font-bold mb-6">
              TM
            </div>
            <h2 className="text-2xl font-bold mb-1">Bienvenue</h2>
            <p className="text-gray-400 mb-6">sur TalentMatch</p>
            
            <p className="text-gray-400 mb-6">
              Plateforme de recrutement intelligente<br />basée sur l'IA
            </p>
            
            <ul className="text-left w-full space-y-3">
              <li className="flex items-center text-gray-300">
                <span className="text-purple-500 mr-2">•</span> Matching IA précis
              </li>
              <li className="flex items-center text-gray-300">
                <span className="text-purple-500 mr-2">•</span> Importation LinkedIn
              </li>
              <li className="flex items-center text-gray-300">
                <span className="text-purple-500 mr-2">•</span> Assistance ChatGPT
              </li>
            </ul>
            
            <div className="mt-6 text-gray-400">
              Pas encore inscrit?
              <div className="mt-2">
                <button className="bg-purple-500 hover:bg-purple-600 text-white px-8 py-2 rounded-full font-medium transition-all hover:-translate-y-1 hover:shadow-lg">
                  S'inscrire
                </button>
              </div>
            </div>
          </div>
          
          {/* Right Side */}
          <div className="w-3/5 p-10">
            <div className="flex mb-8">
              <button 
                className={`px-6 py-2 rounded-md ${activeTab === 'connexion' ? 'bg-purple-500 text-white' : 'text-gray-400'}`}
                onClick={() => setActiveTab('connexion')}
              >
                Connexion
              </button>
              <button 
                className={`px-6 py-2 rounded-md ${activeTab === 'recruteur' ? 'bg-purple-500 text-white' : 'text-gray-400'}`}
                onClick={() => setActiveTab('recruteur')}
              >
                Recruteur
              </button>
              <button 
                className={`px-6 py-2 rounded-md ${activeTab === 'candidat' ? 'bg-purple-500 text-white' : 'text-gray-400'}`}
                onClick={() => setActiveTab('candidat')}
              >
                Candidat
              </button>
            </div>

            <h2 className="text-2xl font-bold mb-8">Connexion à votre compte</h2>

            <form onSubmit={handleSubmit}>
              <div className="mb-6">
                <label className="block text-gray-400 mb-2">Email</label>
                <input
                  type="email"
                  name="email"
                  className="w-full p-3 bg-opacity-10 bg-white border border-gray-700 rounded-lg text-white focus:border-purple-500 focus:outline-none"
                  placeholder="exemple@email.com"
                  value={formData.email}
                  onChange={handleChange}
                />
              </div>

              <div className="mb-6">
                <label className="block text-gray-400 mb-2">Mot de passe</label>
                <input
                  type="password"
                  name="password"
                  className="w-full p-3 bg-opacity-10 bg-white border border-gray-700 rounded-lg text-white focus:border-purple-500 focus:outline-none"
                  placeholder="********"
                  value={formData.password}
                  onChange={handleChange}
                />
              </div>

              <div className="flex items-center mb-6">
                <div className="flex items-center">
                  <input
                    type="checkbox"
                    id="rememberMe"
                    name="rememberMe"
                    checked={formData.rememberMe}
                    onChange={handleChange}
                    className="w-4 h-4 accent-purple-500"
                  />
                  <label htmlFor="rememberMe" className="ml-2 text-gray-400 text-sm">
                    Se souvenir de moi
                  </label>
                </div>
                <a href="#" className="ml-auto text-purple-500 text-sm">Mot de passe oublié?</a>
              </div>

              <button 
                type="submit" 
                className="w-full bg-purple-500 hover:bg-purple-600 text-white py-3 rounded-lg font-medium transition-all hover:-translate-y-1 hover:shadow-lg"
              >
                Se connecter
              </button>
            </form>

            <div className="relative flex items-center justify-center my-6">
              <div className="absolute left-0 right-0 h-px bg-gray-700"></div>
              <span className="relative px-4 bg-gray-900 text-gray-400 text-sm">Ou se connecter avec</span>
            </div>

            <div className="flex justify-center gap-4">
              <button className="w-12 h-12 rounded-full bg-blue-600 flex items-center justify-center hover:-translate-y-1 transition-transform">
                <span className="text-lg">f</span>
              </button>
              <button className="w-12 h-12 rounded-full bg-blue-400 flex items-center justify-center hover:-translate-y-1 transition-transform">
                <span className="text-lg">t</span>
              </button>
              <button className="w-12 h-12 rounded-full bg-red-500 flex items-center justify-center hover:-translate-y-1 transition-transform">
                <span className="text-lg">G</span>
              </button>
            </div>
          </div>
        </div>
      </div>
      
      {/* Footer */}
      <div className="absolute bottom-8 left-0 right-0 text-center text-gray-400 text-sm">
        Je suis...<br />
        <a href="#" className="text-purple-500 font-medium">Recruteur</a>
      </div>
    </div>
  );
};

export default TalentMatchDemo;
