import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const LoginForm = () => {
  const [activeTab, setActiveTab] = useState('connexion');
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    rememberMe: false
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ici, vous pouvez intégrer Firebase pour l'authentification
    console.log('Form submitted:', formData);
  };

  return (
    <div className="login-right">
      <div className="login-tabs">
        <button 
          className={`login-tab ${activeTab === 'connexion' ? 'active' : ''}`}
          onClick={() => setActiveTab('connexion')}
        >
          Connexion
        </button>
        <button 
          className={`login-tab ${activeTab === 'recruteur' ? 'active' : ''}`}
          onClick={() => setActiveTab('recruteur')}
        >
          Recruteur
        </button>
        <button 
          className={`login-tab ${activeTab === 'candidat' ? 'active' : ''}`}
          onClick={() => setActiveTab('candidat')}
        >
          Candidat
        </button>
      </div>

      <h2>Connexion à votre compte</h2>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            className="form-control"
            placeholder="exemple@email.com"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Mot de passe</label>
          <input
            type="password"
            id="password"
            name="password"
            className="form-control"
            placeholder="********"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-check">
          <label className="custom-checkbox">
            <input
              type="checkbox"
              name="rememberMe"
              checked={formData.rememberMe}
              onChange={handleChange}
            />
            <span className="checkmark"></span>
            Se souvenir de moi
          </label>
          <a href="/forgot-password" className="forgot-password">Mot de passe oublié?</a>
        </div>

        <button type="submit" className="login-btn">
          Se connecter
        </button>
      </form>

      <div className="login-divider">
        <span>Ou se connecter avec</span>
      </div>

      <div className="social-login">
        <button className="social-btn facebook-btn">
          <i className="fab fa-facebook-f"></i>
        </button>
        <button className="social-btn twitter-btn">
          <i className="fab fa-twitter"></i>
        </button>
        <button className="social-btn google-btn">
          <i className="fab fa-google"></i>
        </button>
      </div>
    </div>
  );
};

export default LoginForm;
