import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';

const LinkedInProfile = () => {
  const location = useLocation();
  const navigate = useNavigate();
  
  // Récupérer les données LinkedIn de l'état de navigation ou du sessionStorage
  const [linkedInData, setLinkedInData] = useState(null);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    headline: '',
    location: '',
    industry: '',
    bio: '',
    skills: '',
    password: '',
    confirmPassword: ''
  });
  const [passwordError, setPasswordError] = useState('');
  
  useEffect(() => {
    // Récupérer les données LinkedIn
    let data = location.state?.linkedInData;
    
    // Si aucune donnée n'a été passée via location.state, essayer de récupérer depuis sessionStorage
    if (!data) {
      const storedData = sessionStorage.getItem('linkedin_profile_data');
      if (storedData) {
        data = JSON.parse(storedData);
      } else {
        // Rediriger vers la page d'inscription si aucune donnée n'est disponible
        navigate('/candidate-signup');
        return;
      }
    }
    
    setLinkedInData(data);
    
    // Préremplir le formulaire avec les données LinkedIn
    setFormData(prevData => ({
      ...prevData,
      firstName: data.firstName || '',
      lastName: data.lastName || '',
      email: data.email || '',
      headline: data.headline || '',
      location: data.location || '',
      industry: data.industry || ''
    }));
  }, [location, navigate]);
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
    
    // Réinitialiser l'erreur de mot de passe si ces champs sont modifiés
    if (name === 'password' || name === 'confirmPassword') {
      setPasswordError('');
    }
  };
  
  const validatePasswords = () => {
    if (formData.password !== formData.confirmPassword) {
      setPasswordError('Les mots de passe ne correspondent pas');
      return false;
    }
    
    if (formData.password.length < 8) {
      setPasswordError('Le mot de passe doit contenir au moins 8 caractères');
      return false;
    }
    
    setPasswordError('');
    return true;
  };
  
  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!validatePasswords()) {
      return;
    }
    
    // Ici vous intégreriez la logique pour créer un compte avec les données du formulaire
    console.log('Formulaire soumis avec les données LinkedIn:', formData);
    
    // Simuler la création du compte
    alert('Compte créé avec succès!');
    
    // Nettoyer sessionStorage
    sessionStorage.removeItem('linkedin_profile_data');
    sessionStorage.removeItem('linkedin_state');
    
    // Rediriger vers le tableau de bord
    navigate('/dashboard');
  };
  
  if (!linkedInData) {
    return (
      <>
        <Navbar />
        <div className="linkedin-profile-container">
          <div className="linkedin-profile-card">
            <h2>Chargement...</h2>
            <div className="loader"></div>
          </div>
        </div>
      </>
    );
  }
  
  return (
    <>
      <Navbar />
      <div className="linkedin-profile-container">
        <div className="linkedin-profile-card">
          <h2>Compléter votre profil</h2>
          <p className="linkedin-profile-intro">
            Vos informations ont été importées depuis LinkedIn. Veuillez compléter votre profil pour finaliser votre inscription.
          </p>
          
          <div className="linkedin-profile-banner">
            <div className="profile-picture">
              {linkedInData.profilePicture ? (
                <img src={linkedInData.profilePicture} alt="Profile" />
              ) : (
                <div className="profile-initials">
                  {formData.firstName.charAt(0)}{formData.lastName.charAt(0)}
                </div>
              )}
            </div>
            <div className="linkedin-badge">
              <i className="linkedin-icon"></i>
              Importé de LinkedIn
            </div>
          </div>
          
          <form onSubmit={handleSubmit}>
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="firstName">Prénom</label>
                <input
                  type="text"
                  id="firstName"
                  name="firstName"
                  className="form-control"
                  value={formData.firstName}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="lastName">Nom</label>
                <input
                  type="text"
                  id="lastName"
                  name="lastName"
                  className="form-control"
                  value={formData.lastName}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  className="form-control"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="location">Localisation</label>
                <input
                  type="text"
                  id="location"
                  name="location"
                  className="form-control"
                  value={formData.location}
                  onChange={handleChange}
                />
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="headline">Titre professionnel</label>
                <input
                  type="text"
                  id="headline"
                  name="headline"
                  className="form-control"
                  value={formData.headline}
                  onChange={handleChange}
                />
              </div>
              <div className="form-group">
                <label htmlFor="industry">Secteur d'activité</label>
                <input
                  type="text"
                  id="industry"
                  name="industry"
                  className="form-control"
                  value={formData.industry}
                  onChange={handleChange}
                />
              </div>
            </div>
            
            <div className="form-group">
              <label htmlFor="bio">Biographie</label>
              <textarea
                id="bio"
                name="bio"
                className="form-control"
                value={formData.bio}
                onChange={handleChange}
                rows="4"
                placeholder="Parlez-nous de vous et de votre parcours professionnel..."
              ></textarea>
            </div>
            
            <div className="form-group">
              <label htmlFor="skills">Compétences</label>
              <textarea
                id="skills"
                name="skills"
                className="form-control"
                value={formData.skills}
                onChange={handleChange}
                rows="3"
                placeholder="Vos compétences principales (séparées par des virgules)..."
              ></textarea>
            </div>
            
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="password">Mot de passe</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  className="form-control"
                  value={formData.password}
                  onChange={handleChange}
                  required
                  placeholder="Au moins 8 caractères"
                />
              </div>
              <div className="form-group">
                <label htmlFor="confirmPassword">Confirmer le mot de passe</label>
                <input
                  type="password"
                  id="confirmPassword"
                  name="confirmPassword"
                  className="form-control"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            
            {passwordError && (
              <div className="password-error">
                {passwordError}
              </div>
            )}
            
            <div className="form-actions">
              <button type="button" className="cancel-btn" onClick={() => navigate('/candidate-signup')}>
                Annuler
              </button>
              <button type="submit" className="create-profile-btn">
                Créer mon profil
              </button>
            </div>
          </form>
        </div>
      </div>
    </>
  );
};

export default LinkedInProfile;
